const ADD_TO_CART = 'cart/ADD_TO_CART';
export default function cartReducer(state = {}, action) {
    console.log()
    Object.freeze(state);
    const nextState = {...state};
    switch (action.type) {
        case ADD_TO_CART:
            console.log(action.cartItem.id, 'action')
            console.log(nextState, 'nextCart')
            nextState.cart[action.cartItem.id] = action.cartItem;

            return nextState;
        default:
            return state;

    }
}

// action creator
export function addToCart(cartItem) {

    return {
       type: ADD_TO_CART,
       cartItem
    }

}
