const ADD_TO_CART = 'cart/ADD_TO_CART';
export default function cartReducer(state = {}, action) {
    Object.freeze(state);
    const nextState = {...state};
    switch (action.type) {
        case ADD_TO_CART:
            const newCart = nextState.cart;
            next[action.cartItem.id] = action.cartItem;
            return newObject;
        default:
            return state;

    }
}

// action creator
export function addToCart(cartItem) {

    return {
       type: ADD_TO_CART,
       cartItem
    }

}
