const ADD_TO_CART = 'cart/ADD_TO_CART';
export default function cartReducer(state = {}, action) {
    switch (action.type) {
        case ADD_TO_CART:
            const newObject = {}
            action.cart.forEach((produce)=>{
                newObject[produce.id]=produce;
            })
            return newObject;
        default:
            return state;

    }
}

// action creator
export function addToCart(cart) {

    return {
       type: ADD_TO_CART,
       cart
    }

}
